tables.table_schema:
CREATE TABLE Pathogens (
  pathogen_id SERIAL PRIMARY KEY,
  pathogen_name VARCHAR(255) NOT NULL,
  genetic_profile VARCHAR(500) NOT NULL
);

CREATE TABLE ResistanceTrends (
  trend_id SERIAL PRIMARY KEY,
  pathogen_id INT REFERENCES Pathogens(pathogen_id),
  trend_date DATE NOT NULL,
  resistance_level VARCHAR(50) NOT NULL
);
