tables.table_schema:
CREATE TABLE drug_trials (
  trial_id INT PRIMARY KEY,
  drug_name VARCHAR(50),
  efficacy_score FLOAT,
  outcome TEXT
);

CREATE TABLE treatment_protocols (
  protocol_id INT PRIMARY KEY,
  protocol_name VARCHAR(50),
  effectiveness_rating FLOAT,
  notes TEXT
);

CREATE TABLE drugs (
    drug_id INT PRIMARY KEY,
    drug_name TEXT,
    dosage FLOAT
);

CREATE TABLE outcomes (
    outcome_id INT PRIMARY KEY,
    outcome_name TEXT
);

CREATE TABLE treatments (
    treatment_id INT PRIMARY KEY,
    drug_id INT,
    outcome_id INT,
    treatment_protocol TEXT
);

CREATE TABLE Drugs (
  drug_id INT PRIMARY KEY,
  drug_name VARCHAR(255),
  efficacy_score FLOAT
);

CREATE TABLE Patients (
  patient_id INT PRIMARY KEY,
  patient_name VARCHAR(255),
  age INT,
  gender VARCHAR(10)
);

CREATE TABLE Treatments (
  treatment_id INT PRIMARY KEY,
  treatment_name VARCHAR(255),
  duration_in_weeks INT
);

CREATE TABLE Drugs (
   drug_id SERIAL PRIMARY KEY,
   drug_name VARCHAR(255) NOT NULL,
   drug_type VARCHAR(100) NOT NULL,
   production_date DATE
);

CREATE TABLE Treatments (
   treatment_id SERIAL PRIMARY KEY,
   treatment_name VARCHAR(255) NOT NULL,
   cost DECIMAL(10, 2),
   effectiveness_score INT
);

CREATE TABLE Comparison_Outcomes (
   comparison_id SERIAL PRIMARY KEY,
   outcome_id INT REFERENCES Drug_Outcomes(outcome_id),
   comparison_description TEXT
);
