tables.table_schema:
CREATE TABLE patients (
  patient_id INT PRIMARY KEY,
  patient_name VARCHAR(50),
  date_of_birth DATE,
  diagnosis TEXT
);

CREATE TABLE access_controls (
  control_id INT PRIMARY KEY,
  role VARCHAR(50),
  access_level INT
);

CREATE TABLE AccessControls (
  role_id INT PRIMARY KEY,
  role_name VARCHAR(50),
  permissions TEXT
);
