CREATE TABLE patient_medication_adherence (
  patient_id INT,
  medication_id INT,
  dispensed_timestamp TIMESTAMP,
  adherence_status BOOLEAN
);

CREATE TABLE patient_medication (
    patient_id INT PRIMARY KEY,
    medication_name VARCHAR(50),
    medication_dosage INT,
    last_medication_timestamp TIMESTAMP
);

CREATE TABLE patients (
	patient_id INT PRIMARY KEY,
	patient_name VARCHAR(50),
	patient_age INT,
	patient_gender VARCHAR(10)
);

CREATE TABLE medications (
	medication_id INT PRIMARY KEY,
	medication_name VARCHAR(50),
	medication_dosage VARCHAR(20)
);

CREATE TABLE pill_dispensers (
	dispenser_id INT PRIMARY KEY,
	dispenser_name VARCHAR(50),
	location VARCHAR(50)
);

