tables.table_schema:
CREATE TABLE glucose_levels (
  patient_id INT,
  glucose_level FLOAT,
  recorded_time TIMESTAMP
);

CREATE TABLE patient_glucose (
    patient_id INT NOT NULL,
    glucose_level DECIMAL NOT NULL,
    record_time TIMESTAMP NOT NULL,
    PRIMARY KEY (patient_id, record_time)
);

CREATE TABLE device_info (
    device_id INT NOT NULL,
    device_name VARCHAR(50),
    device_location VARCHAR(100),
    PRIMARY KEY (device_id)
);

CREATE TABLE patients (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(50) NOT NULL,
    age INT,
    gender VARCHAR(10)
);
CREATE TABLE glucose_levels (
    glucose_record_id INT PRIMARY KEY,
    patient_id INT REFERENCES patients(patient_id),
    glucose_level DECIMAL,
    record_timestamp TIMESTAMP
);
CREATE TABLE devices (
    device_id INT PRIMARY KEY,
    device_type VARCHAR(50),
    manufacturer VARCHAR(50),
    device_serial_number VARCHAR(20) UNIQUE
);

CREATE TABLE devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(255),
  manufacturer VARCHAR(255),
  firmware_version VARCHAR(20)
);
