tables.table_schema:
CREATE TABLE insurance_claim_data (
  member_id INT,
  claim_date DATE,
  claim_amount DECIMAL(10, 2)
);

CREATE TABLE risk_assessment (
  member_id INT,
  age INT,
  pre_existing_conditions BOOLEAN,
  risk_score DECIMAL(5, 2)
);

CREATE TABLE insurer { 
  insurer_id INT PRIMARY KEY, 
  insurer_name VARCHAR(255) NOT NULL, 
  location VARCHAR(255), 
  policy_type VARCHAR(50) 
}; 

CREATE TABLE claim_history { 
  claim_id INT PRIMARY KEY, 
  insurer_id INT, 
  claim_amount DECIMAL(10, 2), 
  claim_date DATE, 
  FOREIGN KEY (insurer_id) REFERENCES insurer(insurer_id) 
}; 

CREATE TABLE risk_assessment { 
  assessment_id INT PRIMARY KEY, 
  claim_id INT, 
  risk_score DECIMAL(5, 2), 
  assessment_date DATE, 
  FOREIGN KEY (claim_id) REFERENCES claim_history(claim_id) 
}

CREATE TABLE Provider (
    provider_id UUID PRIMARY KEY,
    provider_name VARCHAR(50),
    provider_type VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE insurers ( 
   insurer_id INT PRIMARY KEY, 
   insurer_name VARCHAR(255) NOT NULL, 
   business_type VARCHAR(100), 
   address VARCHAR(255) 
);

CREATE TABLE claims ( 
   claim_id INT PRIMARY KEY, 
   policyholder_id INT, 
   claim_date DATE, 
   claim_amount DECIMAL(10,2) 
);

CREATE TABLE assessments ( 
   assessment_id INT PRIMARY KEY, 
   claim_id INT, 
   risk_score DECIMAL(5,2) 
);

CREATE TABLE predictions ( 
   prediction_id INT PRIMARY KEY, 
   assessment_id INT, 
   predicted_cost DECIMAL(10,2) 
);
