tables.table_schema:
CREATE TABLE insurers (
    insurer_id INT PRIMARY KEY,
    insurer_name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE outliers (
    outlier_id INT PRIMARY KEY,
    claim_id INT,
    reason VARCHAR(255),
    FOREIGN KEY (claim_id) REFERENCES claims(claim_id)
);

CREATE TABLE claims (
    claim_id UUID PRIMARY KEY,
    policy_id UUID,
    claim_amount DECIMAL,
    claim_date DATE
);

CREATE TABLE policyholders (
    policy_id UUID PRIMARY KEY,
    policyholder_name VARCHAR(50),
    policyholder_age INT,
    policyholder_address VARCHAR(100)
);

CREATE TABLE fraud_attempts (
    claim_id UUID PRIMARY KEY,
    fraud_type VARCHAR(20),
    fraud_description TEXT,
    fraud_status BOOLEAN
);

CREATE TABLE claim_details (
    claim_id UUID PRIMARY KEY,
    claim_status VARCHAR(20),
    claim_notes TEXT,
    claim_review_date DATE
);
