tables.table_schema:
CREATE TABLE decision_support (
   id INT PRIMARY KEY,
   patient_id INT,
   guideline TEXT,
   decision TEXT
);

CREATE TABLE Clinical_Decision_Support (
  id INT PRIMARY KEY,
  patient_id INT,
  doctor_id INT
);

CREATE TABLE Guidelines (
  guideline_id INT PRIMARY KEY,
  guideline_description TEXT,
  guideline_link VARCHAR(255)
);

CREATE TABLE Consultations (
  consult_id INT PRIMARY KEY,
  patient_id INT,
  doctor_id INT,
  consultation_date DATE
);

CREATE TABLE Diagnoses (
  diagnosis_id INT PRIMARY KEY,
  patient_id INT,
  diagnosis TEXT,
  diagnosis_date DATE
);

CREATE TABLE guideline_category (
    category_id INT PRIMARY KEY,
    category_name TEXT
);

CREATE TABLE guideline (
    guideline_id INT PRIMARY KEY,
    guideline_name TEXT,
    category_id INT REFERENCES guideline_category(category_id)
);

CREATE TABLE recommendation (
    recommendation_id INT PRIMARY KEY,
    guideline_id INT REFERENCES guideline(guideline_id),
    recommendation_text TEXT
);

CREATE TABLE patient (
    patient_id INT PRIMARY KEY,
    patient_name TEXT,
    patient_age INT,
    patient_gender TEXT
);

CREATE TABLE patient_status (
    status_id INT PRIMARY KEY,
    patient_id INT REFERENCES patient(patient_id),
    status TEXT
);
