tables.table_schema:
CREATE TABLE customer_feedback (
    feedback_id SERIAL PRIMARY KEY,
    customer_id INT,
    support_rating INT,
    comments TEXT
);

CREATE TABLE support_services (
    service_id SERIAL PRIMARY KEY,
    service_name VARCHAR(50),
    service_description TEXT
);

CREATE TABLE CustomerFeedback (
  feedback_id INT PRIMARY KEY,
  customer_id INT,
  service_rating INT,
  feedback_comment TEXT
);

CREATE TABLE ServiceSupport (
  support_id INT PRIMARY KEY,
  service_category VARCHAR(50),
  support_date DATE,
  support_agent_id INT
);

CREATE TABLE CustomerRating (
  rating_id INT PRIMARY KEY,
  customer_id INT,
  overall_rating INT,
  customer_comment TEXT
);

CREATE TABLE SupportServices (
  service_id SERIAL PRIMARY KEY,
  service_name TEXT,
  service_description TEXT
);

CREATE TABLE Customer (
  customer_id SERIAL PRIMARY KEY,
  customer_name TEXT,
  customer_email TEXT
);

CREATE TABLE Ratings (
  rating_id SERIAL PRIMARY KEY,
  feedback_id INT,
  stars INT,
  comment TEXT
);

CREATE TABLE ServiceFeedback (
  feedback_id SERIAL PRIMARY KEY,
  service_id INT,
  rating INT,
  feedback_text TEXT
);
