tables.table_schema:
CREATE TABLE product_manuals (
    manual_id INT PRIMARY KEY,
    manual_name VARCHAR(255),
    manual_content TEXT
);

CREATE TABLE ProductManuals (
    manual_id INT PRIMARY KEY,
    manual_name VARCHAR(100) NOT NULL,
    product_name VARCHAR(100) NOT NULL,
    manual_content TEXT
);

CREATE TABLE TroubleshootingGuides (
    guide_id INT PRIMARY KEY,
    guide_name VARCHAR(100) NOT NULL,
    product_name VARCHAR(100) NOT NULL,
    guide_content TEXT
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(100) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE AccessLogs (
    log_id INT PRIMARY KEY,
    customer_id INT,
    access_time TIMESTAMP,
    FOREIGN KEY (customer_id) REFERENCES Customers(customer_id)
);

CREATE TABLE ProductManuals ( 
	product_id UUID PRIMARY KEY, 
	product_name VARCHAR(255), 
	product_description TEXT, 
	manual_content TEXT 
);

CREATE TABLE TroubleshootingGuides ( 
	guide_id UUID PRIMARY KEY, 
	guide_title VARCHAR(255), 
	guide_content TEXT, 
	created_at TIMESTAMP 
);

CREATE TABLE Customers ( 
	customer_id UUID PRIMARY KEY, 
	customer_name VARCHAR(255), 
	customer_email VARCHAR(255), 
	created_at TIMESTAMP 
);

CREATE TABLE Subscriptions ( 
	subscription_id UUID PRIMARY KEY, 
	customer_id UUID, 
	start_date DATE, 
	end_date DATE 
);
