tables.table_schema:
CREATE TABLE email_scheduling (
    customer_id UUID PRIMARY KEY,
    email_address VARCHAR(255),
    email_time TIMESTAMP,
    time_zone VARCHAR(50)
);

CREATE TABLE customer_preferences (
    customer_id INT PRIMARY KEY,
    email_preference VARCHAR(50),
    time_zone VARCHAR(50)
);

CREATE TABLE email_scheduling (
    email_id INT PRIMARY KEY,
    customer_id INT,
    scheduled_time TIMESTAMP,
    FOREIGN KEY (customer_id) REFERENCES customer_preferences(customer_id)
);

CREATE TABLE customer_preferences (
    customer_id INT PRIMARY KEY,
    email_frequency VARCHAR(50),
    email_type VARCHAR(50)
);

CREATE TABLE customer_timezones (
    customer_id INT PRIMARY KEY,
    timezone VARCHAR(50)
);

CREATE TABLE scheduled_emails (
    email_id INT PRIMARY KEY,
    customer_id INT,
    email_subject VARCHAR(50),
    scheduled_time TIMESTAMP
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    email VARCHAR(255) NOT NULL,
    timezone VARCHAR(50) NOT NULL
);

CREATE TABLE time_zones (
    timezone_id INT PRIMARY KEY,
    timezone_name VARCHAR(50) NOT NULL
);

CREATE TABLE customer_preferences (
  customer_id INT PRIMARY KEY,
  email_frequency VARCHAR(50),
  email_content_preference TEXT,
  timezone VARCHAR(50)
);

CREATE TABLE email_logs (
  log_id INT PRIMARY KEY,
  customer_id INT,
  email_type VARCHAR(50),
  sent_time TIMESTAMP
);

CREATE TABLE email_templates (
  template_id INT PRIMARY KEY,
  template_name VARCHAR(100),
  template_content TEXT
);

CREATE TABLE time_zones (
  zone_id INT PRIMARY KEY,
  zone_name VARCHAR(50),
  utc_offset VARCHAR(10)
);

CREATE TABLE email_schedule (
  schedule_id INT PRIMARY KEY,
  customer_id INT,
  template_id INT,
  send_time TIMESTAMP
);
