tables.table_schema:
CREATE TABLE sentiment_analysis_data (
    id SERIAL PRIMARY KEY,
    sentiment_score FLOAT,
    email_content TEXT
);

CREATE TABLE tailored_email_responses (
    id SERIAL PRIMARY KEY,
    sentiment_score_range VARCHAR(10),
    response_content TEXT
);

CREATE TABLE SentimentAnalysisData (
    id INT PRIMARY KEY,
    email VARCHAR(255),
    sentiment_score INT,
    sentiment_label VARCHAR(50)
);

CREATE TABLE EmailResponses (
    id INT PRIMARY KEY,
    response_text TEXT,
    response_category VARCHAR(50)
);

CREATE TABLE SentimentEmailMapping (
    id INT PRIMARY KEY,
    sentiment_label VARCHAR(50),
    response_category VARCHAR(50)
);

CREATE TABLE SentimentAnalysisResults (
    id INT PRIMARY KEY,
    sentiment_score INT,
    sentiment_type TEXT
);

CREATE TABLE CustomerDetails (
    id INT PRIMARY KEY,
    customer_name TEXT,
    email_address TEXT,
    contact_number TEXT
);

CREATE TABLE sentiment_analysis_data (
    id INT NOT NULL,
    email VARCHAR(255) NOT NULL,
    sentiment_score INT NOT NULL,
    sentiment_label VARCHAR(50) NOT NULL
);

CREATE TABLE email_responses (
    response_id INT NOT NULL,
    email VARCHAR(255) NOT NULL,
    response_text TEXT NOT NULL
);

CREATE TABLE email_templates (
    template_id INT NOT NULL,
    template_subject VARCHAR(255) NOT NULL,
    template_body TEXT NOT NULL
);

CREATE TABLE user_info (
    user_id INT NOT NULL,
    email VARCHAR(255) NOT NULL,
    name VARCHAR(100) NOT NULL,
    job_title VARCHAR(100) NOT NULL
);

CREATE TABLE sentiment_categories (
    category_id INT NOT NULL,
    category_name VARCHAR(100) NOT NULL
);
