CREATE TABLE support_calls (
    call_id SERIAL PRIMARY KEY,
    start_timestamp TIMESTAMP NOT NULL,
    end_timestamp TIMESTAMP NOT NULL,
    staff_id INT NOT NULL
);

CREATE TABLE timestamps (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    call_duration INT
);

CREATE TABLE scheduling (
    id INT PRIMARY KEY,
    staff_id INT,
    call_id INT,
    schedule_date DATE
);

CREATE TABLE staffing (
    id INT PRIMARY KEY,
    staff_id INT,
    availability BOOLEAN
);

CREATE TABLE support_call_schedule (schedule_id SERIAL PRIMARY KEY,
  timestamp_id INT REFERENCES timestamps(timestamp_id),
  duration_id INT REFERENCES call_durations(duration_id));
