tables.table_schema:
CREATE TABLE voice_biometrics (
    user_id UUID PRIMARY KEY,
    voice_profile BYTEA NOT NULL,
    created_at TIMESTAMPTZ
);

CREATE TABLE voice_biometrics_data (
    user_id INT PRIMARY KEY,
    voice_data VARCHAR(255),
    voice_recording_path VARCHAR(255),
    recording_date TIMESTAMP
);

CREATE TABLE authentication_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    login_time TIMESTAMP,
    logout_time TIMESTAMP
);

CREATE TABLE voice_authentication (
    id INT PRIMARY KEY,
    caller_id VARCHAR(255),
    voice_data BYTEA,
    timestamp TIMESTAMP
);

CREATE TABLE support_agents (
    agent_id INT PRIMARY KEY,
    name VARCHAR(255),
    department VARCHAR(255),
    voice_profile BYTEA
);

CREATE TABLE verification_logs (
    log_id INT PRIMARY KEY,
    caller_id VARCHAR(255),
    timestamp TIMESTAMP,
    result VARCHAR(50)
);

CREATE TABLE voice_biometrics_data (
    id SERIAL PRIMARY KEY,
    user_id INT,
    voice_sample BYTEA,
    sample_date TIMESTAMP
);

CREATE TABLE user_profiles (
    user_id INT PRIMARY KEY,
    voice_print BYTEA,
    voice_auth_enabled BOOLEAN
);

CREATE TABLE support_calls (
    call_id SERIAL PRIMARY KEY,
    user_id INT,
    call_date TIMESTAMP,
    call_duration INT
);

CREATE TABLE call_transcripts (
    transcript_id SERIAL PRIMARY KEY,
    call_id INT,
    transcript_text TEXT
);

CREATE TABLE voice_users (
  user_id UUID PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  voice_template BYTEA NOT NULL
);

CREATE TABLE voice_authentication (
  auth_id UUID PRIMARY KEY,
  user_id UUID NOT NULL,
  voice_sample BYTEA NOT NULL,
  authentication_result BOOLEAN NOT NULL
);

CREATE TABLE voice_fraud_detection (
  detection_id UUID PRIMARY KEY,
  call_id UUID NOT NULL,
  fraud_score INT NOT NULL,
  detection_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE voice_support_agents (
  agent_id UUID PRIMARY KEY,
  agent_name VARCHAR(50) NOT NULL,
  department VARCHAR(50) NOT NULL,
  voice_password BYTEA NOT NULL
);
