tables.table_schema:
CREATE TABLE refund_requests (
  refund_id INT PRIMARY KEY,
  order_id INT,
  refund_reason TEXT,
  refund_date DATE
);

CREATE TABLE exchange_requests (
  exchange_id INT PRIMARY KEY,
  order_id INT,
  new_ticket_type VARCHAR(50),
  exchange_date DATE
);

CREATE TABLE ticket_refunds (
    refund_id INT PRIMARY KEY,
    ticket_id INT NOT NULL,
    refund_amount DECIMAL NOT NULL,
    refund_date DATE NOT NULL
);

CREATE TABLE ticket_exchanges (
    exchange_id INT PRIMARY KEY,
    original_ticket_id INT NOT NULL,
    new_ticket_id INT NOT NULL,
    exchange_date DATE NOT NULL
);

CREATE TABLE customer_details (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    customer_email VARCHAR(50) NOT NULL,
    customer_phone VARCHAR(15) NOT NULL
);

CREATE TABLE refund_reasons (
    reason_id INT PRIMARY KEY,
    reason_description VARCHAR(200) NOT NULL
);

CREATE TABLE Customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(255),
  email VARCHAR(255),
  phone_number VARCHAR(20)
);
