tables.table_schema:
CREATE TABLE SentimentData (
    id INT PRIMARY KEY,
    message TEXT,
    sentiment_score FLOAT
);

CREATE TABLE ChatbotResponses (
    id INT PRIMARY KEY,
    response_text TEXT,
    emotion_category VARCHAR(50)
);

CREATE TABLE sentiments (
    user_id UUID,
    timestamp TIMESTAMP,
    sentiment_score FLOAT
);

CREATE TABLE users (
    user_id UUID,
    username STRING,
    email STRING
);

CREATE TABLE sentiments (
    sentiment_id UUID PRIMARY KEY,
    message TEXT,
    sentiment_score FLOAT
);

CREATE TABLE chatbot_responses (
    response_id UUID PRIMARY KEY,
    response_text TEXT,
    response_sentiment FLOAT
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE user_sentiments (
    user_sentiment_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    sentiment_id UUID REFERENCES sentiments(sentiment_id)
);
