tables.table_schema:
CREATE TABLE customer_feedback (
  customer_id INT,
  feedback TEXT
);

CREATE TABLE customer_demographics (
  customer_id INT,
  age INT,
  gender TEXT
);

CREATE TABLE customer_feedback (
    feedback_id INT PRIMARY KEY,
    customer_id INT,
    feedback_text TEXT,
    date_submitted DATE
);

CREATE TABLE customer_demographics (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    age INT,
    city VARCHAR(50)
);

CREATE TABLE customer_join (
    customer_id INT PRIMARY KEY,
    feedback_id INT,
    score INT,
    JOIN (customer_id, feedback_id) REFERENCES customer_feedback(customer_id, feedback_id)
);

CREATE TABLE feedback (
    feedback_id INT PRIMARY KEY,
    customer_id INT,
    feedback_text TEXT,
    feedback_date DATE
);
