tables.table_schema:
CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50) NOT NULL,
  email VARCHAR(100) UNIQUE,
  phone_number VARCHAR(15) 
);

CREATE TABLE CustomerFeedback (
    feedback_id SERIAL PRIMARY KEY,
    feedback_text TEXT NOT NULL,
    customer_id INT REFERENCES Customers(customer_id),
    timestamp TIMESTAMP
);

CREATE TABLE Customers (
    customer_id SERIAL PRIMARY KEY,
    customer_name TEXT NOT NULL,
    email TEXT UNIQUE,
    phone_number TEXT
);

CREATE TABLE FeedbackRating (
    feedback_id INT REFERENCES CustomerFeedback(feedback_id),
    rating INT CHECK (rating >= 1 AND rating <= 5)
);

CREATE TABLE customer_feedback (
    feedback_id INT PRIMARY KEY,
    customer_id INT NOT NULL,
    feedback_text TEXT NOT NULL,
    rating INT CHECK (rating >= 1 AND rating <= 5)
);

CREATE TABLE customer (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    email VARCHAR(50) UNIQUE
);

CREATE TABLE feedback_category (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL
);

CREATE TABLE feedback_history (
    history_id INT PRIMARY KEY,
    feedback_id INT NOT NULL,
    date_submitted DATE NOT NULL,
    CONSTRAINT fk_feedback_id FOREIGN KEY (feedback_id) REFERENCES customer_feedback(feedback_id) ON DELETE CASCADE
);
