CREATE TABLE customer_sentiment_tracking (
    customer_id INT,
    interaction_date DATE,
    interaction_type VARCHAR(50),
    sentiment_score FLOAT
);

CREATE TABLE customers (
  id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  sentiment_score INT
);

CREATE TABLE social_media_interactions (
  interaction_id INT PRIMARY KEY,
  customer_id INT,
  interaction_date TIMESTAMP,
  interaction_text TEXT
);

CREATE TABLE CustomerSentiments (
    customer_id INT PRIMARY KEY,
    sentiment_score FLOAT,
    interaction_timestamp TIMESTAMP
);

CREATE TABLE CustomerInteractions (
    interaction_id INT PRIMARY KEY,
    customer_id INT,
    interaction_type VARCHAR(50),
    interaction_content TEXT
);

CREATE TABLE SatisfactionLevels (
    satisfaction_id INT PRIMARY KEY,
    customer_id INT,
    satisfaction_score FLOAT,
    survey_timestamp TIMESTAMP
);

CREATE TABLE customers (
    customer_id SERIAL PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    social_media_handle VARCHAR(50) NOT NULL
);

CREATE TABLE posts (
    post_id SERIAL PRIMARY KEY,
    post_content TEXT NOT NULL,
    post_date TIMESTAMP NOT NULL,
    customer_id INT REFERENCES customers(customer_id)
);

CREATE TABLE comments (
    comment_id SERIAL PRIMARY KEY,
    comment_text TEXT NOT NULL,
    comment_date TIMESTAMP NOT NULL,
    post_id INT REFERENCES posts(post_id)
);

CREATE TABLE sentiment_analysis (
    analysis_id SERIAL PRIMARY KEY,
    sentiment_score FLOAT NOT NULL,
    analysis_date DATE NOT NULL,
    post_id INT REFERENCES posts(post_id)
);

CREATE TABLE customer_sentiments (
    customer_id INT PRIMARY KEY,
    sentiment_score FLOAT,
    interaction_date TIMESTAMP,
    interaction_type VARCHAR(50)
);

CREATE TABLE social_media_posts (
    post_id INT PRIMARY KEY,
    customer_id INT,
    post_content TEXT,
    post_date DATE
);

CREATE TABLE sentiments_analysis (
    analysis_id INT PRIMARY KEY,
    customer_id INT,
    sentiment_score FLOAT,
    analysis_date DATE
);

CREATE TABLE interactions_history (
    interaction_id INT PRIMARY KEY,
    customer_id INT,
    interaction_type VARCHAR(50),
    interaction_date TIMESTAMP
);

CREATE TABLE customer_feedback (
    feedback_id INT PRIMARY KEY,
    customer_id INT,
    feedback_text TEXT,
    feedback_date DATE
);
