tables.table_schema:
CREATE TABLE Influential_Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    social_media_platform VARCHAR(50),
    followers_count INT
);

CREATE TABLE influential_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    followers_count INT,
    engagement_score FLOAT
);

CREATE TABLE brand_promoters (
    promoter_id INT PRIMARY KEY,
    user_id INT REFERENCES influential_users(user_id),
    brand_id INT NOT NULL,
    promotion_type VARCHAR(50)
);

CREATE TABLE customer_support_agents (
    agent_id INT PRIMARY KEY,
    user_id INT REFERENCES influential_users(user_id),
    expertise_area VARCHAR(100) NOT NULL
);
