tables.table_schema:
CREATE TABLE competitors_mentions (
	social_media_platform VARCHAR(50),
	mention_content TEXT,
	mention_date TIMESTAMP
);

CREATE TABLE market_trends (
	trend_id SERIAL PRIMARY KEY,
	trend_topic VARCHAR(100),
	trend_description TEXT,
	sentiment_score FLOAT
);

CREATE TABLE competitor_mentions (
    id INT PRIMARY KEY,
    mention_text TEXT NOT NULL,
    mention_date TIMESTAMP NOT NULL,
    mention_source VARCHAR(255) NOT NULL
);

CREATE TABLE competitor_handles (
    handle_id INT PRIMARY KEY,
    handle_name VARCHAR(255) NOT NULL,
    handle_user_id INT NOT NULL,
    handle_verified BOOLEAN NOT NULL
);

CREATE TABLE competitor_sentiment (
    sentiment_id INT PRIMARY KEY,
    mention_id INT NOT NULL,
    sentiment_score FLOAT NOT NULL
);

CREATE TABLE competitor_trends (
    trend_id INT PRIMARY KEY,
    trend_topic VARCHAR(255) NOT NULL,
    trend_date TIMESTAMP NOT NULL,
    trend_volume INT NOT NULL
);

CREATE TABLE competitors (
  id INT PRIMARY KEY,
  competitor_name VARCHAR(255) NOT NULL,
  mention_date DATE NOT NULL,
  mention_content TEXT
);

CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(255) NOT NULL,
  email VARCHAR(255),
  phone_number VARCHAR(20)
);

CREATE TABLE locations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(255) NOT NULL,
  city VARCHAR(100),
  country VARCHAR(100)
);
