tables.table_schema:
CREATE TABLE multimedia_content (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    type VARCHAR(50),
    content BYTEA
);

CREATE TABLE multimedia_content (
    id INT PRIMARY KEY,
    filename TEXT,
    file_type TEXT,
    file_size INT
);

CREATE TABLE images (
  id INT PRIMARY KEY,
  image_url TEXT,
  image_title TEXT,
  image_description TEXT
);

CREATE TABLE videos (
  id INT PRIMARY KEY,
  video_url TEXT,
  video_title TEXT,
  video_description TEXT
);

CREATE TABLE documents (
  id INT PRIMARY KEY,
  document_url TEXT,
  document_title TEXT,
  document_description TEXT
);
