tables.table_schema:
CREATE TABLE PurchaseHistory (
    purchase_id INT PRIMARY KEY,
    customer_id INT,
    amount DECIMAL(10, 2),
    purchase_date DATE
);

CREATE TABLE engagement_levels (
    customer_id INT PRIMARY KEY,
    email_open_rate DECIMAL(5, 2),
    click_through_rate DECIMAL(5, 2),
    conversion_rate DECIMAL(5, 2)
);
