tables.table_schema:
CREATE TABLE chatbot_training_data (
    training_id INT PRIMARY KEY,
    input_text VARCHAR(255),
    output_text VARCHAR(255),
    training_timestamp TIMESTAMP
);

CREATE TABLE chatbot_training_data (
    user_id INT PRIMARY KEY,
    question VARCHAR(255),
    answer TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE chatbot_metadata (
    model_version INT PRIMARY KEY,
    accuracy FLOAT,
    training_date DATE,
    deployment_status BOOLEAN
);

CREATE TABLE training_data (
    message_id INT PRIMARY KEY,
    message_text TEXT,
    intent TEXT,
    entity TEXT
);

CREATE TABLE bot_responses (
    response_id INT PRIMARY KEY,
    response_text TEXT,
    intent TEXT,
    confidence FLOAT
);

CREATE TABLE intent_mapping (
    intent_id INT PRIMARY KEY,
    intent_name TEXT,
    related_intents JSON,
    examples TEXT[]
);
