tables.table_schema:
CREATE TABLE performance_metrics (
  agent_id INT NOT NULL,
  response_time FLOAT NOT NULL,
  customer_satisfaction_rating INT NOT NULL
);

CREATE TABLE agent_metrics (
    agent_id INT PRIMARY KEY,
    response_time INT,
    customer_satisfaction FLOAT
);

CREATE TABLE agent_performance_metrics (
    agent_id INT PRIMARY KEY,
    response_time INT NOT NULL,
    customer_satisfaction_rating FLOAT
);

CREATE TABLE customer_interactions (
    interaction_id INT PRIMARY KEY,
    agent_id INT NOT NULL,
    customer_id INT NOT NULL,
    interaction_date TIMESTAMP NOT NULL
);

CREATE TABLE agent_ratings (
    agent_id INT PRIMARY KEY,
    average_rating FLOAT,
    total_ratings INT
);

CREATE TABLE agents (
  agent_id INT PRIMARY KEY,
  agent_name VARCHAR(50),
  response_time FLOAT,
  customer_satisfaction_rating FLOAT
);

CREATE TABLE requests (
  request_id INT PRIMARY KEY,
  agent_id INT,
  request_type VARCHAR(50),
  request_time TIMESTAMP
);

CREATE TABLE responses (
  response_id INT PRIMARY KEY,
  request_id INT,
  response_content TEXT,
  response_time INT
);

CREATE TABLE ratings (
  rating_id INT PRIMARY KEY,
  agent_id INT,
  customer_id INT,
  satisfaction_rating FLOAT
);

CREATE TABLE agents (
  agent_id SERIAL PRIMARY KEY,
  agent_name VARCHAR(50) NOT NULL,
  response_time FLOAT NOT NULL
);

CREATE TABLE customers (
  customer_id SERIAL PRIMARY KEY,
  customer_name VARCHAR(50) NOT NULL,
  customer_email VARCHAR(100) NOT NULL
);

CREATE TABLE response_ratings (
  rating_id SERIAL PRIMARY KEY,
  interaction_id INT REFERENCES interactions(interaction_id),
  customer_satisfaction_rating INT NOT NULL,
  response_quality_rating INT NOT NULL
);

CREATE TABLE reports (
  report_id SERIAL PRIMARY KEY,
  agent_id INT REFERENCES agents(agent_id),
  report_date DATE NOT NULL,
  performance_metric VARCHAR(50) NOT NULL
);
