);

CREATE TABLE comments (
    comment_id INT PRIMARY KEY,
    guide_id INT NOT NULL,
    customer_id INT NOT NULL,
    comment_text TEXT NOT NULL,
    FOREIGN KEY (guide_id) REFERENCES troubleshooting_guides(id),
