tables.table_schema:
CREATE TABLE agents (
  agent_id INT PRIMARY KEY,
  agent_name VARCHAR(50) NOT NULL,
  access_level INT
);

CREATE TABLE queries (
  query_id INT PRIMARY KEY,
  query_text TEXT,
  response_text TEXT
);

CREATE TABLE agents (
    agent_id INT PRIMARY KEY,
    agent_name VARCHAR(255) NOT NULL,
    agent_email VARCHAR(255) NOT NULL
);

CREATE TABLE log_history (
    log_id INT PRIMARY KEY,
    query_id INT,
    log_time TIMESTAMP NOT NULL,
    FOREIGN KEY (query_id) REFERENCES queries(query_id)
);

CREATE TABLE VirtualAgents (
 agent_id INT PRIMARY KEY,
 agent_name VARCHAR(255) NOT NULL,
 email VARCHAR(255) UNIQUE,
 phone_number VARCHAR(15)
);

CREATE TABLE Queries (
 query_id INT PRIMARY KEY,
 query_text TEXT,
 response_text TEXT
);

CREATE TABLE Automations (
 automation_id INT PRIMARY KEY,
 automation_name VARCHAR(255) NOT NULL,
 script TEXT
);

CREATE TABLE Logs (
 log_id INT PRIMARY KEY,
 timestamp TIMESTAMP NOT NULL,
 log_text TEXT
);

CREATE TABLE virtual_agents (
    agent_id INT PRIMARY KEY,
    agent_name VARCHAR(50) NOT NULL,
    agent_status VARCHAR(20) NOT NULL
);

CREATE TABLE common_queries (
    query_id INT PRIMARY KEY,
    query_text TEXT NOT NULL,
    response_text TEXT NOT NULL
);

CREATE TABLE agent_logs (
    log_id INT PRIMARY KEY,
    agent_id INT REFERENCES virtual_agents(agent_id),
    query_id INT REFERENCES common_queries(query_id),
    log_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE automated_responses (
    response_id INT PRIMARY KEY,
    agent_id INT REFERENCES virtual_agents(agent_id),
    query_id INT REFERENCES common_queries(query_id),
    response_text TEXT NOT NULL
);

CREATE TABLE agent_statistics (
    stat_id INT PRIMARY KEY,
    agent_id INT REFERENCES virtual_agents(agent_id),
    queries_processed INT NOT NULL,
    response_time_ms INT NOT NULL
);
