tables.table_schema:
CREATE TABLE email_segmentation ( 
 id SERIAL PRIMARY KEY, 
 email_address VARCHAR(255) NOT NULL, 
 behavior_score INT NOT NULL, 
 engagement_level VARCHAR(50) 
);

CREATE TABLE email_lists (
    email_id INT PRIMARY KEY,
    email_address VARCHAR(255),
    user_behavior VARCHAR(50),
    engagement_level VARCHAR(50)
);

CREATE TABLE segment_details (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(255),
    segment_description TEXT
);

CREATE TABLE user_behavior (
    user_id INT PRIMARY KEY,
    email VARCHAR(255),
    behavior_level INT
);

CREATE TABLE engagement_levels (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(50),
    description TEXT
);

CREATE TABLE EmailListSegmentation (
    user_id INT,
    email VARCHAR(255),
    engagement_level INT
);

CREATE TABLE UserBehavior (
    user_id INT,
    behavior_category VARCHAR(50),
    behavior_description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE SegmentationRules (
    rule_id INT,
    rule_name VARCHAR(50),
    condition TEXT
);

CREATE TABLE SegmentMembers (
    segment_id INT,
    user_id INT,
    member_since TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    email VARCHAR(255) NOT NULL,
    engagement_level VARCHAR(50) NOT NULL,
    behavior_type VARCHAR(50) NOT NULL
);

CREATE TABLE email_lists (
    list_id INT PRIMARY KEY,
    list_name VARCHAR(100) NOT NULL,
    segment_criteria VARCHAR(255) NOT NULL
);

CREATE TABLE user_engagement (
    engagement_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    timestamp TIMESTAMP NOT NULL,
    interaction_type VARCHAR(50) NOT NULL
);

CREATE TABLE email_subscriptions (
    subscription_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    list_id INT REFERENCES email_lists(list_id),
    subscribe_date DATE NOT NULL
);
