tables.table_schema:
CREATE TABLE engagement_metrics (
    post_id INT,
    likes INT,
    comments INT,
    shares INT
);

CREATE TABLE posts (
    post_id INT PRIMARY KEY,
    likes INT,
    comments INT,
    shares INT
);

CREATE TABLE likes (
    post_id UUID,
    user_id UUID,
    like_timestamp TIMESTAMP
);

CREATE TABLE comments (
    post_id UUID,
    user_id UUID,
    comment_text TEXT,
    comment_timestamp TIMESTAMP
);

CREATE TABLE shares (
    post_id UUID,
    user_id UUID,
    share_timestamp TIMESTAMP
);

CREATE TABLE likes (
    post_id INT NOT NULL,
    user_id INT NOT NULL,
    like_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE shares (
    share_id INT PRIMARY KEY,
    post_id INT NOT NULL,
    user_id INT NOT NULL,
    share_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE post_likes (
    post_id INT PRIMARY KEY,
    user_id INT,
    like_timestamp TIMESTAMP
);

CREATE TABLE post_shares (
    share_id INT PRIMARY KEY,
    post_id INT,
    user_id INT,
    share_timestamp TIMESTAMP
);

CREATE TABLE post_performance (
    post_id INT PRIMARY KEY,
    performance_date DATE,
    total_engagement INT,
    performance_rank INT
);
