tables.table_schema:
CREATE TABLE follower_growth_data (
    user_id INT PRIMARY KEY,
    follower_count INT NOT NULL,
    engagement_rate FLOAT NOT NULL,
    date_recorded DATE NOT NULL
);

CREATE TABLE follower_growth (
    user_id INT,
    date DATE,
    followers_count INT
);

CREATE TABLE engagement_rates (
    user_id INT,
    date DATE,
    likes_count INT,
    comments_count INT
);

CREATE TABLE follower_growth (
    id INT PRIMARY KEY,
    date DATE,
    followers_count INT,
    growth_rate FLOAT
);

CREATE TABLE social_media_accounts (
    id INT PRIMARY KEY,
    account_name VARCHAR(255),
    handle VARCHAR(50),
    account_type VARCHAR(50)
);

CREATE TABLE follower_growth (
  id INT PRIMARY KEY,
  date DATE NOT NULL,
  followers INT NOT NULL
);

CREATE TABLE engagement_rate (
  id INT PRIMARY KEY,
  date DATE NOT NULL,
  engagement_rate FLOAT NOT NULL
);

CREATE TABLE social_media_strategy (
  id INT PRIMARY KEY,
  strategy_name VARCHAR(255) NOT NULL,
  strategy_description TEXT NOT NULL
);

CREATE TABLE strategy_performance (
  id INT PRIMARY KEY,
  strategy_id INT NOT NULL,
  date DATE NOT NULL,
  engagement_rate FLOAT NOT NULL
);

CREATE TABLE campaign_details (
  id INT PRIMARY KEY,
  campaign_name VARCHAR(255) NOT NULL,
  start_date DATE NOT NULL,
  end_date DATE NOT NULL
);
