tables.table_schema:
CREATE TABLE content_calendar (
    id INT PRIMARY KEY,
    post_content TEXT,
    post_date TIMESTAMP,
    social_media_platform VARCHAR(50)
);

CREATE TABLE social_media_platforms (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR(50)
);

CREATE TABLE ContentCalendar (
    post_id INT PRIMARY KEY,
    post_content TEXT,
    post_date TIMESTAMP,
    social_media_platform VARCHAR(50)
);

CREATE TABLE SocialMediaPlatforms (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR(50)
);

CREATE TABLE ScheduledPosts (
    schedule_id INT PRIMARY KEY,
    post_id INT,
    scheduled_time TIMESTAMP,
    FOREIGN KEY (post_id) REFERENCES ContentCalendar(post_id)
);

CREATE TABLE Posts (
	post_id INT,
	post_content TEXT,
	post_date DATE,
	social_media_platform VARCHAR(50)
);

CREATE TABLE Users (
	user_id INT,
	username VARCHAR(50),
	email VARCHAR(100)
);

CREATE TABLE Platforms (
	platform_id INT,
	platform_name VARCHAR(50)
);

CREATE TABLE Schedules (
	schedule_id INT,
	post_id INT,
	platform_id INT,
	schedule_date DATE
);
