tables.table_schema:
CREATE TABLE sentiment_analysis_data (
    post_id UUID PRIMARY KEY,
    post_content TEXT,
    sentiment_score INT
);

CREATE TABLE sentiment_data (
  post_id SERIAL PRIMARY KEY,
  post_text TEXT,
  sentiment_score FLOAT
);

CREATE TABLE user_data (
  user_id SERIAL PRIMARY KEY,
  username VARCHAR(255),
  followers_count INT,
  sentiment_score FLOAT
);

CREATE TABLE sentiment_analysis_posts (
    post_id INT PRIMARY KEY,
    post_content TEXT,
    user_id INT,
    sentiment_score FLOAT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE sentiment_results (
    result_id INT PRIMARY KEY,
    post_id INT,
    sentiment_type VARCHAR(20),
    confidence_level FLOAT
);

CREATE TABLE sentiment_scores (
    post_id INT PRIMARY KEY,
    sentiment_score FLOAT
);

CREATE TABLE post_details (
    post_id INT PRIMARY KEY,
    post_text TEXT,
    author_id INT,
    post_date TIMESTAMP
);

CREATE TABLE author_details (
    author_id INT PRIMARY KEY,
    author_name TEXT,
    followers_count INT
);

CREATE TABLE sentiment_categories (
    category_id INT PRIMARY KEY,
    category_name TEXT,
    category_description TEXT
);

CREATE TABLE Sentiments (
    sentiment_id INT PRIMARY KEY,
    post_id INT,
    sentiment_score FLOAT,
    sentiment_text TEXT
);

CREATE TABLE Posts (
    post_id INT PRIMARY KEY,
    user_id INT,
    post_date TIMESTAMP,
    post_content TEXT
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    user_type VARCHAR(20)
);

CREATE TABLE UserSentiments (
    user_sentiment_id INT PRIMARY KEY,
    user_id INT,
    sentiment_id INT
);

CREATE TABLE SentimentAnalysis (
    analysis_id INT PRIMARY KEY,
    sentiment_id INT,
    analysis_result TEXT
);
