tables.table_schema:
CREATE TABLE social_media_trends (
    id INT PRIMARY KEY,
    hashtag VARCHAR(255),
    trend_date DATE,
    trend_time TIME
);

CREATE TABLE Hashtags (
  hashtag_id SERIAL PRIMARY KEY,
  hashtag_text VARCHAR(255) NOT NULL,
  trending_rank INT,
  created_at TIMESTAMP
);

CREATE TABLE SocialMediaPosts (
    post_id INT PRIMARY KEY,
    post_content TEXT,
    post_author TEXT,
    post_date TIMESTAMP
);

CREATE TABLE Hashtags (
    hashtag_id INT PRIMARY KEY,
    hashtag_name VARCHAR(50) UNIQUE
);

CREATE TABLE TrendingHashtags (
    hashtag_id UUID PRIMARY KEY,
    hashtag_name TEXT NOT NULL,
    post_count INT
);

CREATE TABLE UserProfiles (
    user_id UUID PRIMARY KEY,
    username TEXT NOT NULL,
    followers_count INT,
    following_count INT
);

CREATE TABLE HashtagUsage (
    usage_id UUID PRIMARY KEY,
    post_id UUID REFERENCES SocialMediaPosts(post_id),
    hashtag_id UUID REFERENCES TrendingHashtags(hashtag_id)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(50) UNIQUE NOT NULL,
    followers INT
);

CREATE TABLE hashtags (
    hashtag_id INT PRIMARY KEY,
    hashtag_name VARCHAR(30) UNIQUE NOT NULL
);

CREATE TABLE post_hashtags (
    post_id INT REFERENCES posts(post_id),
    hashtag_id INT REFERENCES hashtags(hashtag_id),
    PRIMARY KEY(post_id, hashtag_id)
);

CREATE TABLE trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(50) UNIQUE NOT NULL,
    trend_description TEXT
);
