tables.table_schema:
CREATE TABLE CustomerInteractions (
  interaction_id INT PRIMARY KEY,
  customer_id INT,
  ad_id INT,
  timestamp TIMESTAMP
);

CREATE TABLE Customers (
  customer_id INT PRIMARY KEY,
  name VARCHAR(50),
  email VARCHAR(100)
);

CREATE TABLE Ads (
  ad_id INT PRIMARY KEY,
  ad_name VARCHAR(100),
  ad_type VARCHAR(50),
  ad_cost DECIMAL
);

CREATE TABLE TargetingData (
  customer_id INT,
  ad_id INT,
  location VARCHAR(50),
  age INT
);

CREATE TABLE ad_interaction (
  ad_id INT PRIMARY KEY,
  customer_id INT,
  interaction_type VARCHAR(50),
  interaction_date TIMESTAMP
);
