tables.table_schema:
CREATE TABLE campaign_performance_data (
  campaign_id INT PRIMARY KEY,
  clicks INT,
  impressions INT,
  cost DECIMAL
);

CREATE TABLE campaign_optimization (
  campaign_id INT PRIMARY KEY,
  conversion_rate DECIMAL,
  return_on_investment DECIMAL,
  budget DECIMAL
);

CREATE TABLE campaign_costs (
    id INT,
    campaign_id INT,
    cost DECIMAL(10,2),
    date DATE
);

CREATE TABLE campaign_audience (
    id INT,
    campaign_id INT,
    audience_segment VARCHAR(255),
    audience_size INT
);

CREATE TABLE campaign_conversions (
    id INT,
    campaign_id INT,
    conversion_type VARCHAR(50),
    conversion_count INT
);

CREATE TABLE campaign_clicks (
    id INT,
    campaign_id INT,
    click_timestamp TIMESTAMP,
    device_type VARCHAR(50)
);
