tables.table_schema:
CREATE TABLE AudienceSegments ( 
    segment_id UUID PRIMARY KEY, 
    segment_name VARCHAR(100), 
    audience_size INT, 
    description TEXT 
);

CREATE TABLE AudienceSegments (
  id UUID PRIMARY KEY,
  segment_name VARCHAR(255),
  segment_description TEXT
);

CREATE TABLE SegmentMembers (
  member_id UUID,
  segment_id UUID,
  join_date DATE,
  PRIMARY KEY (member_id, segment_id)
);

CREATE TABLE SegmentMetrics (
  metric_id SERIAL PRIMARY KEY,
  segment_id UUID,
  metric_name VARCHAR(255),
  metric_value FLOAT
);

CREATE TABLE AudienceSegments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE SegmentAttributes (
    attribute_id INT PRIMARY KEY,
    attribute_name VARCHAR(50) NOT NULL,
    type VARCHAR(20) NOT NULL
);

CREATE TABLE audience_segments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(255),
    segment_description TEXT
);

CREATE TABLE behavior_data (
    member_id INT,
    purchase_history TEXT,
    behavior_score INT,
    FOREIGN KEY (member_id) REFERENCES audience_members(member_id)
);
