tables.table_schema:
CREATE TABLE ad_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE conversion_rates (
    conversion_id INT PRIMARY KEY,
    ad_id INT REFERENCES ad_campaigns(campaign_id),
    conversion_date DATE,
    conversion_value DECIMAL(10, 2)
);

CREATE TABLE ad_campaigns (
    id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE ad_clicks (
    id INT PRIMARY KEY,
    ad_id INT,
    click_time TIMESTAMP,
    conversion BOOLEAN
);

CREATE TABLE ad_impressions (
    id INT PRIMARY KEY,
    ad_id INT,
    impression_time TIMESTAMP,
    viewer_id INT
);

CREATE TABLE ad_viewers (
    id INT PRIMARY KEY,
    viewer_name VARCHAR(255),
    country VARCHAR(100),
    age INT
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(255)
);
