CREATE TABLE creatives (
  creative_id UUID PRIMARY KEY,
  creative_name VARCHAR(255) NOT NULL,
  creative_type VARCHAR(50) NOT NULL,
  image_url VARCHAR(255) NOT NULL
);

CREATE TABLE ad_placements (
  placement_id UUID PRIMARY KEY,
  placement_name VARCHAR(255) NOT NULL,
  placement_type VARCHAR(50) NOT NULL
);

CREATE TABLE ad_campaigns (
  campaign_id UUID PRIMARY KEY,
  campaign_name VARCHAR(255) NOT NULL
);

CREATE TABLE ad_groups (
  group_id UUID PRIMARY KEY,
  group_name VARCHAR(255) NOT NULL,
  campaign_id UUID REFERENCES ad_campaigns(campaign_id) NOT NULL
);

CREATE TABLE ad_targets (
  target_id UUID PRIMARY KEY,
  target_name VARCHAR(255) NOT NULL,
  group_id UUID REFERENCES ad_groups(group_id) NOT NULL,
  placement_id UUID REFERENCES ad_placements(placement_id) NOT NULL
);
