tables.table_schema:
CREATE TABLE keyword_data (
    id SERIAL PRIMARY KEY,
    keyword VARCHAR(255) NOT NULL,
    bids DECIMAL NOT NULL,
    status VARCHAR(20) NOT NULL
);

CREATE TABLE search_query_data (
    id SERIAL PRIMARY KEY,
    search_query TEXT NOT NULL,
    search_volume INT NOT NULL,
    match_type VARCHAR(20) NOT NULL
);

CREATE TABLE ad_group_data (
    id SERIAL PRIMARY KEY,
    ad_group_name VARCHAR(50) NOT NULL,
    campaign_id INT NOT NULL,
    status VARCHAR(20) NOT NULL
);

CREATE TABLE keyword_data (
    keyword_id INT PRIMARY KEY,
    keyword_name VARCHAR(255) NOT NULL,
    campaign_id INT NOT NULL,
    keyword_status VARCHAR(50) DEFAULT 'active'
);

CREATE TABLE search_query_data (
    search_query_id INT PRIMARY KEY,
    search_query VARCHAR(255) NOT NULL,
    keyword_id INT NOT NULL,
    search_query_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE campaign_data (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255) NOT NULL,
    campaign_budget DECIMAL(10, 2) NOT NULL,
    campaign_status VARCHAR(50) DEFAULT 'active'
);

CREATE TABLE ad_group_data (
    ad_group_id INT PRIMARY KEY,
    ad_group_name VARCHAR(255) NOT NULL,
    campaign_id INT NOT NULL,
    ad_group_status VARCHAR(50) DEFAULT 'active'
);

CREATE TABLE ad_data (
    ad_id INT PRIMARY KEY,
    ad_title VARCHAR(255) NOT NULL,
    ad_group_id INT NOT NULL,
    ad_status VARCHAR(50) DEFAULT 'active'
);
