tables.table_schema:
CREATE TABLE ad_campaigns (
  campaign_id INT PRIMARY KEY,
  campaign_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE ad_impressions (
  impression_id INT PRIMARY KEY,
  ad_id INT,
  viewer_location VARCHAR(100),
  timestamp TIMESTAMP
);

CREATE TABLE campaigns (
  id INT PRIMARY KEY,
  campaign_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE users (
  id INT PRIMARY KEY,
  user_name VARCHAR(255),
  email VARCHAR(255)
);

CREATE TABLE ad_groups (
  id INT PRIMARY KEY,
  group_name VARCHAR(255),
  budget DECIMAL(10,2),
  status VARCHAR(20)
);

CREATE TABLE regions (
  id INT PRIMARY KEY,
  region_name VARCHAR(100),
  country VARCHAR(100)
);
