tables.table_schema:
CREATE TABLE email_opened (
    opening_id SERIAL PRIMARY KEY,
    interaction_id INT REFERENCES email_interactions(interaction_id),
    opened_timestamp TIMESTAMP
);

CREATE TABLE email_clicked (
    clicking_id SERIAL PRIMARY KEY,
    interaction_id INT REFERENCES email_interactions(interaction_id),
    clicked_url VARCHAR(255),
    click_timestamp TIMESTAMP
);
