tables.table_schema:
CREATE TABLE ProductRecommendations (
    user_id INT,
    product_id INT,
    recommendation_score FLOAT,
    timestamp TIMESTAMPTZ
);

CREATE TABLE UserRecommendations (
    user_id UUID PRIMARY KEY,
    product_id UUID,
    recommendation_score FLOAT
);

CREATE TABLE PurchaseHistory (
    purchase_id UUID PRIMARY KEY,
    user_id UUID,
    product_id UUID,
    purchase_date TIMESTAMP
);

CREATE TABLE users (user_id UUID PRIMARY KEY, name STRING, email STRING);
CREATE TABLE products (product_id UUID PRIMARY KEY, name STRING, category STRING);
CREATE TABLE purchases (purchase_id UUID PRIMARY KEY, user_id UUID REFERENCES users(user_id), product_id UUID REFERENCES products(product_id), purchase_date TIMESTAMPTZ);
CREATE TABLE recommendations (recommendation_id UUID PRIMARY KEY, user_id UUID REFERENCES users(user_id), recommended_product_id UUID REFERENCES products(product_id), recommendation_score FLOAT);
CREATE TABLE interactions (interaction_id UUID PRIMARY KEY, user_id UUID REFERENCES users(user_id), product_id UUID REFERENCES products(product_id), interaction_type STRING);
