tables.table_schema:
CREATE TABLE advertising_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE click_conversion_data (
    click_id INT PRIMARY KEY,
    campaign_id INT,
    click_date TIMESTAMP,
    conversion_status BOOLEAN
);

CREATE TABLE ad_data (
    ad_id INT PRIMARY KEY,
    ad_name VARCHAR(255),
    ad_type VARCHAR(50),
    ad_cost DECIMAL
);

CREATE TABLE conversion_data (
    conversion_id INT PRIMARY KEY,
    click_id INT,
    conversion_time TIMESTAMP,
    conversion_amount DECIMAL
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    user_name VARCHAR(255),
    email VARCHAR(255),
    age INT
);

CREATE TABLE campaign (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(50),
    start_date DATE,
    end_date DATE
);

CREATE TABLE ad_group (
    ad_group_id INT PRIMARY KEY,
    ad_group_name VARCHAR(50),
    campaign_id INT,
    FOREIGN KEY (campaign_id) REFERENCES campaign(campaign_id)
);
