tables.table_schema:
CREATE TABLE email_subject_ab_testing (
    email_id UUID PRIMARY KEY,
    subject_line VARCHAR(255) NOT NULL,
    open_rate DECIMAL(5,2) NOT NULL,
    test_group INT NOT NULL
);

CREATE TABLE email_subjects (
    id INT PRIMARY KEY,
    subject_line TEXT,
    open_rate FLOAT
);

CREATE TABLE ab_test_results (
    test_id INT,
    subject_line_id INT,
    open_rate_variation FLOAT
);

CREATE TABLE email_subject_lines (
  experiment_id INT PRIMARY KEY,
  subject_line VARCHAR(255),
  open_rate FLOAT
);

CREATE TABLE user_profiles (
  user_id INT PRIMARY KEY,
  email VARCHAR(255),
  name VARCHAR(100),
  subscription_date DATE
);

CREATE TABLE email_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255) NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL
);

CREATE TABLE email_subjects (
    subject_id INT PRIMARY KEY,
    subject_line VARCHAR(255) NOT NULL,
    campaign_id INT NOT NULL,
    FOREIGN KEY (campaign_id) REFERENCES email_campaigns(campaign_id)
);

CREATE TABLE emails_sent (
    email_id INT PRIMARY KEY,
    subject_id INT NOT NULL,
    recipient_email VARCHAR(255) NOT NULL,
    sent_date DATETIME NOT NULL,
    opened BOOLEAN NOT NULL
);

CREATE TABLE email_stats (
    stat_id INT PRIMARY KEY,
    email_id INT NOT NULL,
    open_rate DECIMAL(5,2) NOT NULL,
    click_through_rate DECIMAL(5,2) NOT NULL,
    FOREIGN KEY (email_id) REFERENCES emails_sent(email_id)
);

CREATE TABLE email_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL,
    test_group INT NOT NULL,
    CONSTRAINT fk_test_group FOREIGN KEY (test_group) REFERENCES test_groups(group_id)
);

CREATE TABLE email_subjects (
    subject_id INT PRIMARY KEY,
    subject_line VARCHAR NOT NULL,
    campaign_id INT NOT NULL,
    CONSTRAINT fk_campaign_id FOREIGN KEY (campaign_id) REFERENCES email_campaigns(campaign_id)
);

CREATE TABLE test_groups (
    group_id INT PRIMARY KEY,
    group_name VARCHAR NOT NULL
);

CREATE TABLE open_rates (
    open_rate_id INT PRIMARY KEY,
    email_id INT NOT NULL,
    open_date DATE NOT NULL,
    open_time TIME NOT NULL,
    CONSTRAINT fk_email_id FOREIGN KEY (email_id) REFERENCES emails(email_id)
);

CREATE TABLE emails (
    email_id INT PRIMARY KEY,
    subject_id INT NOT NULL,
    recipient_email VARCHAR NOT NULL,
    send_date DATE NOT NULL,
    sent_time TIME NOT NULL,
    CONSTRAINT fk_subject_id FOREIGN KEY (subject_id) REFERENCES email_subjects(subject_id)
);
