tables.table_schema:
CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR,
    email_address VARCHAR
);

CREATE TABLE leads (
    lead_id INT PRIMARY KEY,
    lead_name VARCHAR,
    lead_email VARCHAR,
    lead_status VARCHAR
);

CREATE TABLE campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR,
    start_date DATE,
    end_date DATE
);
