tables.table_schema:
CREATE TABLE A/B_Testing_Data (
    user_id INT PRIMARY KEY,
    campaign_id INT,
    sign_up_date TIMESTAMP,
    conversion_status BOOLEAN
);

CREATE TABLE Campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name TEXT,
    start_date TIMESTAMP,
    end_date TIMESTAMP
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    user_name TEXT,
    email TEXT
);

CREATE TABLE Test_Groups (
    group_id INT PRIMARY KEY,
    group_name TEXT,
    campaign_id INT,
    user_id INT
);
