tables.table_schema:
CREATE TABLE customer_demographics (
    customer_id INT PRIMARY KEY,
    age INT,
    gender VARCHAR(10),
    income FLOAT
);

CREATE TABLE marketing_messages (
    message_id INT PRIMARY KEY,
    message_text TEXT,
    target_group VARCHAR(50),
    delivery_date DATE
);

CREATE TABLE CustomerGroups (group_id INT PRIMARY KEY,
    group_name VARCHAR,
    group_description VARCHAR);
