tables.table_schema:
CREATE TABLE MarketingData (
    marketing_id INT PRIMARY KEY,
    offline_sales DECIMAL,
    online_sales DECIMAL
);

CREATE TABLE OfflineSalesData (
    transaction_id INT PRIMARY KEY,
    customer_id INT,
    amount DECIMAL(10, 2),
    transaction_date DATE
);

CREATE TABLE MarketingCampaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(50),
    start_date DATE,
    end_date DATE
);

CREATE TABLE online_sales (
    order_id INT PRIMARY KEY,
    customer_id INT,
    order_date DATE,
    total_amount DECIMAL
);

CREATE TABLE offline_sales (
    invoice_id INT PRIMARY KEY,
    customer_id INT,
    invoice_date DATE,
    total_amount DECIMAL
);

CREATE TABLE marketing_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(50),
    start_date DATE,
    end_date DATE
);

CREATE TABLE offline_sales_report (
    report_id INT PRIMARY KEY,
    transaction_date TIMESTAMP,
    total_sales_amount DECIMAL(10, 2)
);

CREATE TABLE online_sales_report (
    report_id INT PRIMARY KEY,
    order_date TIMESTAMP,
    total_sales_amount DECIMAL(10, 2)
);
