);

CREATE TABLE tags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50) NOT NULL
);

CREATE TABLE content_tags (
    content_id UUID PRIMARY KEY,
    tag_name VARCHAR(255) NOT NULL
);

CREATE TABLE tags (
    tag_id UUID PRIMARY KEY,
    tag_name VARCHAR(255) NOT NULL
);

CREATE TABLE tag_relations (
    content_id UUID,
    tag_id UUID,
    PRIMARY KEY (content_id, tag_id)
);

CREATE TABLE ContentTags (
    content_id UUID PRIMARY KEY,
    tag_name TEXT NOT NULL
);

CREATE TABLE ContentCategories (
    category_id UUID PRIMARY KEY,
    category_name TEXT NOT NULL
);

CREATE TABLE ContentTagMapping (
    content_id UUID,
    tag_name TEXT,
    FOREIGN KEY (content_id) REFERENCES ContentTags(content_id),
    FOREIGN KEY (tag_name) REFERENCES ContentTags(tag_name)
);

CREATE TABLE ContentCategoryMapping (
    content_id UUID,
    category_id UUID,
    FOREIGN KEY (content_id) REFERENCES ContentTags(content_id),
    FOREIGN KEY (category_id) REFERENCES ContentCategories(category_id)
);

CREATE TABLE content_tags (
  id SERIAL PRIMARY KEY,
  tag_name TEXT NOT NULL
);

CREATE TABLE categories (
  id SERIAL PRIMARY KEY,
  category_name TEXT NOT NULL
);

CREATE TABLE content_category_map (
  content_id SERIAL,
  category_id SERIAL,
  PRIMARY KEY (content_id, category_id)
);

CREATE TABLE tagged_contents (
  id SERIAL PRIMARY KEY,
  content TEXT NOT NULL,
  tag_id SERIAL
);

CREATE TABLE content_details (
  content_id SERIAL PRIMARY KEY,
  title TEXT NOT NULL,
  description TEXT
);
