tables.table_schema:
CREATE TABLE purchase_frequency (
    customer_id INT PRIMARY KEY,
    frequency INT,
    last_purchase_date DATE,
    FOREIGN KEY (customer_id) REFERENCES customers(customer_id)
);

CREATE TABLE avg_order_value (
    customer_id INT PRIMARY KEY,
    avg_value DECIMAL(10, 2),
    total_orders INT,
    FOREIGN KEY (customer_id) REFERENCES customers(customer_id)
);
